import React from 'react';
import * as d3 from "d3";
import data from './../data/data.csv';

class Card extends React.Component {
	constructor(){
		super();
		this.state={
			text:[],
		}
	}
	componentDidMount() {
		d3.csv(data)
			.then(function(data){
				console.log(data)
				let text = data.forEach((item)=>{
					console.log(item)
					return(
						<div key={item}>
						<h1>{item.quote}</h1>
						</div>
					)
				this.setState({text:text});
				console.log(this.state.text);
				})

			})
			.catch(function(error){

			})
	}

	render() {
		return(
			<div className='card'>
				{this.state.text}
			</div>
		)

	}
}

export default Card
