import React from 'react';
import * as d3 from "d3";
import data from './../data/data.csv';

class Card extends React.Component {

	render() {
		items = d3.csv(data)
				.then(function(data){
					console.log(data)
					data.map((d)=><div className='card'><h1 key={d.key}>{d.quote}</h1></div>)
				})
		return(
		{items }
		)
	}
}

export default Card
