import React from 'react';
import * as d3 from "d3";
import data from './../data/data.csv';

class Card extends React.Component {
  state = {
    items: []
  };

  componentDidMount() {
  	d3.csv(data)
  	.then(function(data){
	    this.setState({
	    		items: data

	    });
  	})
  }

  render() {
    return (
      <div>
        {this.state.items.map(item => (
          <div className="card" key={item.key}>
            <h1>{item.quote}</h1>
          </div>
        ))}
      </div>
    );
  }
}

export default Card
