import React from 'react';
import * as d3 from "d3";

class Card extends React.Component {
	constructor(){
		super();
		this.state={
			text: "Inspirational Message Here",
		}
	}
	componentDidMount() {
		d3.csv('data/data.csv')
			.then(function(data){
				let text = data.forEach((item)=>{
					console.log(item)
					return(
						<h1>{item}</h1>
					)
				})
				this.setState({text:text});
				console.log("state", this.state.text);
			})
	}

	render() {
		return(
			<div className='card'>
				{this.state.text}
			</div>
		)

	}
}

export default Card
