import React from 'react';
import * as d3 from "d3";

class Card extends React.Component {
	constructor(){
		super();
		this.state={
			text: [],
		}
	}
	componentDidMount() {
		d3.csv('data/data.csv')
			.then(function(data){
				data.forEach(function(item){
					return(
						<h1>{item}</h1>
					);
				})
			})
	}

	render() {
		return(
			<div className='card'>
				{this.state.text}
			</div>
		)

	}
}

export default Card
